mapper.select:
select blogId, title,
blogLogo, summary,
blogLogo, summary, content,
blogDate, blogStatus, starQuantity, collectQuantity, commentQuantity, category.categoryId, categoryName from blog left join category on blog.categoryId = category.categoryId where blog.categoryId in ( select categoryId from category where userId = #{userId}
and categoryId = #{categoryId}
)
and blogStatus = #{blogStatus}
order by blogId desc

select blogId, title, blogLogo, summary, blogDate, blogStatus, starQuantity, collectQuantity, commentQuantity, category.categoryId, categoryName, user.userId, username, userLogoSrc from blog left join category on blog.categoryId = category.categoryId left join user on category.userId=user.userId
where blogStatus = #{blogStatus}
order by blogId desc

select
count(*)
from blog
where categoryId in (
select categoryId from category
where userId = #{userId}

    and categoryId = #{categoryId}

)

    and blogStatus = #{blogStatus}

select count(*) from blog left join category on blog.categoryId = category.categoryId left join user on category.userId=user.userId
where blogStatus = #{blogStatus}
order by blogId desc
